import React, { useState } from 'react';

export function Timer() {
    const [count, setCount] = useState(0);


    return (
        <div className="container-fluid mt-5 d-flex justify-content-center align-items-center flex-column">
            <span className="fs-1">{count}</span>
            <div className="buttons mt-5 d-flex ">
                <button type="button" className="btn btn-success d-block m-1" onClick={() => {
                     ///STOPWATCH
                        let time = 0
                        let stopwatch = setInterval(() => {
                            hour = time / 60 / 60 % 60
                            minutes = time / 60 % 60
                            seconds = time % 60
                            count = `${String(Math.trunc(hour)).padStart(2, '0')}:${String(Math.trunc(minutes)).padStart(2, '0')}:${String(Math.trunc(seconds)).padStart(2, '0')}`;
                time++;
                }, 1000)}}>
                Go
                </button>
                <button type="button" className="btn btn-danger d-block m-1">Danger</button>
            </div>
        </div>
        )
}
