import React, { useState, useEffect } from 'react';

export function Timer() {
    const [count, setCount] = useState(0);
    function go() {
        
        useEffect(() => {
            const interval = setInterval(() => { setCount(count + 1) }, 1000);
        return () => clearInterval(interval);
        }, []);
    }
    return (
        <div className="container-fluid mt-5 d-flex justify-content-center align-items-center flex-column">
            <span className="fs-1">{count} seconds left </span>
            <div className="buttons mt-5 d-flex ">
                <button type="button" className="btn btn-success d-block m-1" onClick={go}>
                    Go
                </button>
                <button type="button" className="btn btn-danger d-block m-1">Stop</button>
            </div>
        </div>
        )
}
